mapper.update:
UPDATE coffee 
SET
	COFFEE_ID = coffee_seq.NEXTVAL
	, COFFEE_NAME = #{coffeeName}
	, KCAL_INFO = #{kaclInfo}
	, COFFEE_IMAGE = #{coffeeImage}
	, AMOUNT = #{amount}
	, CATEGORY = #{category}
	, ICE_HOT = #{iceHot}

mapper.insert:
INSERT INTO coffee 
	(	
		COFFEE_ID
		, COFFEE_NAME
		, KCAL_INFO
		, COFFEE_IMAGE
		, AMOUNT
		, CATEGORY
		, ICE_HOT
	)
VALUES 
	(
		coffee_seq.NEXTVAL
		, #{coffeeName}
		, #{kaclInfo}
		, #{coffeeImage}
		, #{amount}
		, #{category}
		, #{iceHot}
	)
